const context = $node["15 - Préparer Contexte Complet1"].json;
const leadContext = $node["9 - Préparer Contexte Lead1"].json;
const message = leadContext?.user_message || '';
const sentimentNode = $node["Sentiment Analyst Agent"];
const sentimentRaw = sentimentNode?.json?.text || '{}';

try {
  const cleanJson = sentimentRaw.replace(/```json\n?|\n?```/g, '').trim();
  const analysis = JSON.parse(cleanJson);
  return [{
    json: {
      ...context,
      user_message: message,
      analysis
    }
  }];
} catch (e) {
  console.error('Error parsing sentiment JSON:', e.message);
  return [{
    json: {
      ...context,
      user_message: message,
      analysis: {
        sentiment: 'neutre',
        urgency: 'basse',
        decision: 'repondre_vite',
        ton_suggeré: 'professionnel'
      }
    }
  }];
}

