// Extraction du code profil si présent dans le message original
const userMessage = $('9 - Préparer Contexte Lead1').first().json.user_message || '';
const profileCodeMatch = userMessage.match(/MAT-\d{4}-\d{3}/i);

// Détection si escalade a eu lieu
const aiResponse = $input.first().json.final_response || '';
const containsEscalation = $input.first().json.contains_escalation || false;

return [{
  json: {
    final_response: aiResponse,
    profile_code: profileCodeMatch ? profileCodeMatch[0].toUpperCase() : null,
    conversation_status: containsEscalation ? 'attente_humain' : 'bot_actif',
    intent: profileCodeMatch ? 'profile_query' : (containsEscalation ? 'escalation' : 'general')
  }
}];

